var jumpToCode = (function init() {
    // Classes of code we would like to highlight in the file view
    var missingCoverageClasses = ['.cbranch-no', '.cstat-no', '.fstat-no'];

    // Elements to highlight in the file listing view
    var fileListingElements = ['td.pct.low'];

    // We don't want to select elements that are direct descendants of another match
    var notSelector = ':not(' + missingCoverageClasses.join('):not(') + ') > '; // becomes `:not(a):not(b) > `

    // Selector that finds elements on the page to which we can jump
    var selector =
        fileListingElements.join(', ') +
        ', ' +
        notSelector +
        missingCoverageClasses.join(', ' + notSelector); // becomes `:not(a):not(b) > a, :not(a):not(b) > b`

    // The NodeList of matching elements
    var missingCoverageElements = document.querySelectorAll(selector);

    var currentIndex;

    function toggleClass(index) {
        missingCoverageElements
            .item(currentIndex)
            .classList.remove('highlighted');
        missingCoverageElements.item(index).classList.add('highlighted');
    }

    function makeCurrent(index) {
        toggleClass(index);
        currentIndex = index;
        missingCoverageElements.item(index).scrollIntoView({
            behavior: 'smooth',
            block: 'center',
            inline: 'center'
        });
    }

    function goToPrevious() {
        var nextIndex = 0;
        if (typeof currentIndex !== 'number' || currentIndex === 0) {
            nextIndex = missingCoverageElements.length - 1;
        } else if (missingCoverageElements.length > 1) {
            nextIndex = currentIndex - 1;
        }

        makeCurrent(nextIndex);
    }

    function goToNext() {
        var nextIndex = 0;

        if (
            typeof currentIndex === 'number' &&
            currentIndex < missingCoverageElements.length - 1
        ) {
            nextIndex = currentIndex + 1;
        }

        makeCurrent(nextIndex);
    }

    return function jump(event) {
        if (
            document.getElementById('fileSearch') === document.activeElement &&
            document.activeElement != null
        ) {
            // if we're currently focused on the search input, we don't want to navigate
            return;
        }

        switch (event.which) {
            case 78: // n
            case 74: // j
                goToNext();
                break;
            case 66: // b
            case 75: // k
            case 80: // p
                goToPrevious();
                break;
        }
    };
})();
window.addEventListener('keydown', jumpToCode);

'use client'

import { zodResolver } from '@hookform/resolvers/zod'
import { useForm } from 'react-hook-form'
import { z } from 'zod'
import { format } from 'date-fns'
import { CalendarIcon } from 'lucide-react'

import { Button } from '@/components/ui/button'
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  FormDescription,
} from '@/components/ui/form'
import { Input } from '@/components/ui/input'
import { Textarea } from '@/components/ui/textarea'
import { Switch } from '@/components/ui/switch'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover'
import { Calendar } from '@/components/ui/calendar'
import { cn } from '@/lib/utils'
import { 
  useCreateOutcome, 
  useAppointments,
  useUpdateReferral 
} from '@/hooks/use-api'
import type { TreatmentOutcome } from '@/types'

const outcomeSchema = z.object({
  referral_id: z.string().min(1),
  appointment_id: z.string().optional().nullable(),
  diagnosis: z.string().min(2, 'Tanı bilgisi gereklidir'),
  treatment_received: z.string().min(2, 'Yapılan işlem/tedavi belirtilmelidir'),
  outcome_notes: z.string().optional(),
  follow_up_needed: z.boolean().default(false),
  follow_up_date: z.date().optional().nullable(),
})

type OutcomeFormValues = z.infer<typeof outcomeSchema>

interface TreatmentOutcomeFormProps {
  referralId: string
  outcome?: TreatmentOutcome
  onSuccess?: () => void
}

export function TreatmentOutcomeForm({ referralId, outcome, onSuccess }: TreatmentOutcomeFormProps) {
  const createOutcome = useCreateOutcome(referralId)
  const updateReferral = useUpdateReferral(referralId)
  const { data: appointments } = useAppointments(referralId)

  const form = useForm({
    resolver: zodResolver(outcomeSchema),
    defaultValues: {
      referral_id: referralId,
      appointment_id: outcome?.appointmentId || '',
      diagnosis: outcome?.diagnosis || '',
      treatment_received: outcome?.treatmentReceived || '',
      outcome_notes: outcome?.outcomeNotes || '',
      follow_up_needed: outcome?.followUpNeeded ?? false,
      follow_up_date: outcome?.followUpDate ? new Date(outcome.followUpDate) : null,
    },
  })

  function onSubmit(values: OutcomeFormValues) {
    createOutcome.mutate({
      referral_id: values.referral_id,
      appointment_id: values.appointment_id || null,
      diagnosis: values.diagnosis,
      treatment_received: values.treatment_received,
      outcome_notes: values.outcome_notes || null,
      follow_up_needed: values.follow_up_needed,
      follow_up_date: values.follow_up_date ? format(values.follow_up_date, 'yyyy-MM-dd') : null,
    }, {
      onSuccess: () => {
        // Automatically update referral status to 'treated' or 'follow-up'
        updateReferral.mutate({ 
          status: values.follow_up_needed ? 'follow-up' : 'treated' 
        })
        onSuccess?.()
      }
    })
  }

  const followUpNeeded = form.watch('follow_up_needed')

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <FormField
          control={form.control}
          name="appointment_id"
          render={({ field }) => (
            <FormItem>
              <FormLabel>İlgili Randevu (Opsiyonel)</FormLabel>
              <Select onValueChange={field.onChange} defaultValue={field.value || ''}>
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Randevu seçiniz" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {appointments?.map((a) => (
                    <SelectItem key={a.id} value={a.id}>
                      {format(new Date(a.appointmentDate), 'dd/MM/yyyy HH:mm')} - {a.location}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="diagnosis"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Tanı / Teşhis</FormLabel>
              <FormControl>
                <Input placeholder="Konulan teşhis" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="treatment_received"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Uygulanan Tedavi / İşlem</FormLabel>
              <FormControl>
                <Textarea placeholder="Yapılan tedavi veya ameliyat detayları" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="outcome_notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Sonuç Notları</FormLabel>
              <FormControl>
                <Textarea placeholder="Tedavi sonucu ile ilgili ek notlar" {...field} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="follow_up_needed"
          render={({ field }) => (
            <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3">
              <div className="space-y-0.5">
                <FormLabel>Kontrol Gerekli mi?</FormLabel>
                <FormDescription className="text-xs">
                  Hastanın ilerleyen tarihte tekrar görülmesi gerekiyor mu?
                </FormDescription>
              </div>
              <FormControl>
                <Switch
                  checked={field.value}
                  onCheckedChange={field.onChange}
                />
              </FormControl>
            </FormItem>
          )}
        />

        {followUpNeeded && (
          <FormField
            control={form.control}
            name="follow_up_date"
            render={({ field }) => (
              <FormItem className="flex flex-col animate-in fade-in slide-in-from-top-2">
                <FormLabel>Kontrol Tarihi</FormLabel>
                <Popover>
                  <PopoverTrigger asChild>
                    <FormControl>
                      <Button
                        variant={"outline"}
                        className={cn(
                          "w-full pl-3 text-left font-normal",
                          !field.value && "text-muted-foreground"
                        )}
                      >
                        {field.value ? (
                          format(field.value, "dd/MM/yyyy")
                        ) : (
                          <span>Tarih seçiniz</span>
                        )}
                        <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                      </Button>
                    </FormControl>
                  </PopoverTrigger>
                  <PopoverContent className="w-auto p-0" align="start">
                    <Calendar
                      mode="single"
                      selected={field.value || undefined}
                      onSelect={field.onChange}
                      disabled={(date) => date < new Date()}
                      initialFocus
                    />
                  </PopoverContent>
                </Popover>
                <FormMessage />
              </FormItem>
            )}
          />
        )}

        <div className="flex justify-end gap-2 pt-4">
          <Button type="submit" disabled={createOutcome.isPending}>
            Belgele
          </Button>
        </div>
      </form>
    </Form>
  )
}

'use client'

import { File, Image as ImageIcon, Upload, X } from 'lucide-react'
import { useCallback, useState } from 'react'
import { toast } from 'sonner'

import { Button } from '@/components/ui/button'
import { cn } from '@/lib/utils'

interface FileUploadProps {
  onFileSelect: (file: File) => void
  accept?: string
  maxSize?: number // in MB
  disabled?: boolean
  className?: string
}

export function FileUpload({
  onFileSelect,
  accept = '.pdf,.jpg,.jpeg,.png,.doc,.docx',
  maxSize = 10,
  disabled = false,
  className,
}: FileUploadProps) {
  const [isDragging, setIsDragging] = useState(false)
  const [selectedFile, setSelectedFile] = useState<File | null>(null)
  const [preview, setPreview] = useState<string | null>(null)

  const validateFile = useCallback(
    (file: File): string | null => {
      // Check file size
      if (file.size > maxSize * 1024 * 1024) {
        return `Dosya boyutu en fazla ${maxSize}MB olabilir`
      }

      // Check file type
      const acceptedTypes = accept.split(',').map((t) => t.trim())
      const fileExt = `.${file.name.split('.').pop()?.toLowerCase()}`
      const isAccepted = acceptedTypes.some((type) => {
        if (type.startsWith('.')) return fileExt === type
        return file.type.startsWith(type.replace('*', ''))
      })

      if (!isAccepted) {
        return 'Desteklenmeyen dosya formatı'
      }

      return null
    },
    [maxSize, accept]
  )

  const handleFile = useCallback(
    (file: File) => {
      const error = validateFile(file)
      if (error) {
        toast.error(error)
        return
      }

      setSelectedFile(file)
      onFileSelect(file)

      // Generate preview for images
      if (file.type.startsWith('image/')) {
        const reader = new FileReader()
        reader.onload = (e) => setPreview(e.target?.result as string)
        reader.readAsDataURL(file)
      } else {
        setPreview(null)
      }
    },
    [onFileSelect, validateFile]
  )

  const handleDrop = useCallback(
    (e: React.DragEvent) => {
      e.preventDefault()
      setIsDragging(false)

      if (disabled) return

      const file = e.dataTransfer.files[0]
      if (file) handleFile(file)
    },
    [disabled, handleFile]
  )

  const handleDragOver = useCallback(
    (e: React.DragEvent) => {
      e.preventDefault()
      if (!disabled) setIsDragging(true)
    },
    [disabled]
  )

  const handleDragLeave = useCallback(() => {
    setIsDragging(false)
  }, [])

  const handleFileInput = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const file = e.target.files?.[0]
      if (file) handleFile(file)
    },
    [handleFile]
  )

  const handleRemove = useCallback(() => {
    setSelectedFile(null)
    setPreview(null)
  }, [])

  const getFileIcon = () => {
    if (!selectedFile) return <Upload className="h-8 w-8" />
    if (selectedFile.type.startsWith('image/'))
      return <ImageIcon className="h-8 w-8" />
    return <File className="h-8 w-8" />
  }

  return (
    <div className={cn('space-y-4', className)}>
      <div
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        className={cn(
          'rounded-lg border-2 border-dashed p-6 transition-colors',
          isDragging && 'border-primary bg-primary/5',
          !isDragging && 'border-border hover:border-primary/50',
          disabled && 'cursor-not-allowed opacity-50',
          !disabled && 'cursor-pointer'
        )}
      >
        {selectedFile ? (
          <div className="flex items-center gap-4">
            {preview ? (
              <img
                src={preview}
                alt="Preview"
                className="h-20 w-20 rounded object-cover"
              />
            ) : (
              <div className="text-muted-foreground">{getFileIcon()}</div>
            )}
            <div className="min-w-0 flex-1">
              <p className="truncate font-medium">{selectedFile.name}</p>
              <p className="text-muted-foreground text-sm">
                {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
              </p>
            </div>
            <Button
              type="button"
              variant="ghost"
              size="icon"
              onClick={handleRemove}
              disabled={disabled}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        ) : (
          <label className="flex cursor-pointer flex-col items-center gap-2">
            <div className="text-muted-foreground">{getFileIcon()}</div>
            <div className="text-center">
              <p className="font-medium">
                Dosya yüklemek için tıklayın veya sürükleyin
              </p>
              <p className="text-muted-foreground mt-1 text-sm">
                Maksimum {maxSize}MB • {accept}
              </p>
            </div>
            <input
              type="file"
              accept={accept}
              onChange={handleFileInput}
              disabled={disabled}
              className="hidden"
            />
          </label>
        )}
      </div>
    </div>
  )
}

